sources.sass\\utils\\_placeholders.scss:
%flex-center-style {
  display: flex;
  align-items: center;
  justify-content: center;
}

%button--pink {
  height: 44px;
  font-weight: 700;
  font-size: 16px;
  line-height: 1.31;

  border: none;
  border-radius: 22px;

  color: $primary-color;
  background-color: $accent-color;

  cursor: pointer;
  transition: box-shadow $transition-duration $transition-timing-function;

  &:hover,
  &:focus {
    box-shadow: 0px 4px 4px rgba($primary-text-color, 0.25);
  }
}

%button--white {
  height: 44px;
  font-weight: 700;
  font-size: 16px;
  line-height: 1.31;

  border: none;
  border-radius: 22px;

  background-color: $primary-color;
  color: $accent-color;

  cursor: pointer;
  transition: box-shadow $transition-duration $transition-timing-function,
    background-color $transition-duration $transition-timing-function;

  &:hover,
  &:focus {
    box-shadow: 0px 4px 4px rgba($primary-text-color, 0.25);
    background-color: $accent-shadow-color;
  }
}

sources.sass\\utils\\_variables.scss:
// Breakpoints
$mobile: 480px;
$tablet: 768px;
$desktop: 1280px;

// Font
$primary-font: 'DM Sans', sans-serif;
$secondary-font: 'Titan One', cursive;

// Colors
$primary-color: #ffffff;
$secondary-color: #fff5f6;

$primary-brand-color: #d41443;
$primary-brand-shade-color: #d41342;
$primary-brand-shadow-color: #e17992;
$secondary-brand-color: #ffb8ca;
$tertiary-brand-color: #ffa5ba;
$second-card-color: #f0d1a5;
$third-card-color: #c2e297;

$primary-text-color: #000000;
$secondary-text-color: #907e82;

$accent-color: #c7133e;
$accent-shadow-color: #ecebeb;
$shadow-background-color: #fafafa;

$border-color: #e1e1e1;
$menu-border-color: #e18298;

// Animation
$transition-duration: 250ms;
$transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);

sources.sass\\components\\_commonstyle.scss:
body {
  color: $primary-text-color;
  background-color: $primary-color;
  font-family: $primary-font;
 .gallery {
   overflow: hidden;
 }
}

ul {
  list-style: none;
  padding: 0;
  margin: 0;
}
h1,
h2,
h3,
h4,
h5,
h6,
p {
  font-weight: 400;
  margin: 0;
  padding: 0;
}

a {
	text-decoration: none;
	cursor: pointer;
}

button {
	padding: 0;
	border: none;
	cursor: pointer;
}

img {
  display: block;
  max-width: 100%;
  height: auto;
}
  /* Скрытие заголовков */
.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  border: 0;
  padding: 0;
  white-space: nowrap;
  clip-path: inset(100%);
  clip: rect(0 0 0 0);
  overflow: hidden;
}

