sources.sass\\components\\_modal-order.scss:
.modal {
  position: absolute;
  top: 0;
  left: 0;
  background-color: $primary-brand-shadow-color;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  z-index: 40;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: $primary-text-color;

  padding: 30px;
  position: fixed;
  @media screen and (max-width: 767px) {
    justify-content: start;
    width: 100vw;
    height: 100vh;
  }
  @media screen and (min-width: $tablet) {
    border-radius: 100%;
    padding: 30px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 600px;
    height: 615px;
  }
}

.input-field {
  margin-bottom: 30px;
}

.input-field__input {
  @include transition (border-color);
  width: 100%;
  padding-left: 35px;
  height: 40px;
  border: 1px solid $primary-brand-shadow-color;
  border-radius: 4px;
  outline: none;

  font-weight: 700;
  font-size: 16px;
  line-height: 1.31;
  transition: border-color $transition-duration $transition-timing-function;

  &:focus + .input-field__icon {
    fill: $primary-text-color;
  }

  &:focus, &:hover {
    border-color: $second-card-color;
  }
}

.input-field__group {
  position: relative;
}

.input-field__icon {
  fill: $primary-brand-shade-color;
  position: absolute;
  top: 50%;
  left: 12px;
  transform: translateY(-50%);
  transition: fill $transition-duration $transition-timing-function;
}

.modal__title {
  font-weight: 700;
  font-size: 20px;
  line-height: 1.15;
  text-align: center;
  letter-spacing: 0.03em;
  color: $second-card-color;
  margin-bottom: 30px;
}

.label {
  font-size: 12px;
  line-height: 1.17;
  letter-spacing: 0.01em;
  margin-bottom: 5px;
  display: block;
}

sources.sass\\components\\_modal-about.scss:
.about-backdrop {
  @extend %flex-center-style
}

.about-form {
  position: relative;
  width: 300px;
  min-height: 400px;
  padding: 40px 28px;
  border-radius: 22px;
  background-color: $primary-color;

  background-image: url(../images/about/icecream-bg4.webp);
  background-repeat: no-repeat;
  background-size: cover;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);

  @media screen and (max-width: 767px) {
    width: 300px;
    min-height: 380px;
  }

  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    width: 350px;
    min-height: 520px;
    padding: 44px 32px;
  }

  @media screen and (min-width: $desktop) {
    width: 400px;
    min-height: 600px;
    padding: 44px 38px;
  }
}

.about-form__title {
  @include font(700, 18px, 1.14, 0.04em);
  color: $primary-brand-color;
  text-align: center;
  margin-bottom: 15px;

}

.about-form__description {
 @include font (500, 16px, 1.14, 0.06em);
 text-align: left;
 @media screen and (min-width: $tablet) {
  @include font (500, 16px, 1.5, 0.06em); 
 }
 @media screen and (min-width: $desktop) {
  @include font (500, 16px, 1.86, 0.06em); 
 }
}

.about-close-button {
  @include transition (transform);
  @include transition (fill);
  @extend %flex-center-style;
  position: absolute;
  top: 20px;
  right: 20px;
  border: none;
  border-radius: 50%;
  padding: 2px;
  background-color: transparent;

  &:hover,
  &:focus {
    .about-form__icon-close {
      transform: scale(1.25);
      fill: $primary-brand-color;
    }
  }
}

.about-form__icon-close {
  transform: scale(1);
  transition: fill $transition-duration $transition-timing-function,
  transform $transition-duration $transition-timing-function;
  fill: $secondary-text-color;
}

sources.sass\\components\\_map-form.scss:
.map-backdrop {
  @extend %flex-center-style;
  padding: 10px;
}

.map-form {
  position: relative;
  border-radius: 22px;
  padding: 10px;

  @media screen and (max-width: 767px) {
    width: 80%;
    height: 80%;
  }

  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    width: 500px;
    height: 500px;
  }

  @media screen and (min-width: $desktop) {
    width: 600px;
    height: 600px;
  }
}

.map-form__content {
  position: relative;
  width: 100%;
  height: 100%;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
}

.map-form__contacts {
  position: absolute;
  z-index: 5;
  bottom: 0;
  left: 0;
  @extend %flex-center-style;
  width: 100%;
  padding: 10px;
  background-color: $primary-color;
}

.map-form__title {
  @extend %flex-center-style;
  font-weight: 700;
  text-transform: uppercase;
  color: $primary-brand-color;

  @media screen and (max-width: 767px) {
    font-size: 14px;
    line-height: 1.31;
    margin-right: 15px;
  }

  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    font-size: 18px;
    line-height: 1.5;
    margin-right: 35px;
  }

  @media screen and (min-width: $desktop) {
    font-size: 20px;
    line-height: 1.86;
    margin-right: 60px;
  }
}

.map-form__list-item {
  @include transition(transform);
  display: flex;
  justify-content: space-between;
  align-items: center;
  transform: scale(1);

  @media screen and (max-width: 767px) {
    &:not(:last-child) {
      margin-bottom: 5px;
    }
  }

  @media screen and (min-width: $tablet) {
    margin-bottom: 10px;
  }

  &:hover {
    transform: scale(1.15);
    .map-form__text {
      color: $primary-brand-color;
    }
    .map-form__link {
      color: $primary-brand-color;
    }
  }
}

.map-form__text {
  font-weight: 500;

  @media screen and (max-width: 479px) {
    font-size: 10px;
    line-height: 1.31;
    margin-right: 3px;
  }

  @media screen and(min-width: $mobile) and (max-width: 767px) {
    font-size: 12px;
    line-height: 1.31;
    margin-right: 5px;
  }

  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    font-size: 14px;
    line-height: 1.5;
    margin-right: 15px;
  }

  @media screen and (min-width: $desktop) {
    font-size: 16px;
    line-height: 1.86;
    margin-right: 25px;
  }
}

.map-form__text--primary {
  color: $second-card-color;
}

.map-form__text--secondary {
  color: $third-card-color;
}

.map-form__link {
  color: $secondary-text-color;

  @media screen and (max-width: 479px) {
    font-size: 7px;
    line-height: 1.31;
  }

  @media screen and (min-width: $mobile) and (max-width: 767px) {
    font-size: 12px;
    line-height: 1.31;
  }

  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    font-size: 14px;
    line-height: 1.5;
  }

  @media screen and (min-width: $desktop) {
    font-size: 16px;
    line-height: 1.86;
  }
}

