  border-radius: 22px;
  background-color: $primary-color;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);

  @media screen and (max-width: 767px) {
    width: 300px;
    min-height: 380px;
  }

  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    min-width: 410px;
    min-height: 390px;
  }

  @media screen and (min-width: $desktop) {
    width: 528px;
    min-height: 400px;
  }
}

.franchise-form__title {
  margin-bottom: 20px;
  text-align: center;
  font-weight: 700;
  color: $primary-brand-color;

  @media screen and (max-width: 767px) {
    font-size: 16px;
    line-height: 1.5;
  }

  @media screen and (min-width: $tablet) {
    font-size: 20px;
    line-height: 1.86;
  }
}

.franchise-form__block {
  @extend %flex-center-style;
  flex-direction: column;
}

.franchise-form__group-absolute {
  position: relative;
  margin-bottom: 20px;
}

.franchise-form__input {
  @include transition (border-color);
  @include transition (color);
  padding: 10px 10px 10px 28px;
  outline: none;
  border: 2px solid $secondary-text-color;
  border-radius: 4px;
  color: $secondary-text-color;

  @media screen and (max-width: 767px) {
    min-width: 200px;
  }

  @media screen and (min-width: $tablet) {
    min-width: 280px;
  }

  &:hover,
  &:focus {
    border-color: $primary-brand-color;
  }

  &:hover + .franchise-form__label,
  &:focus + .franchise-form__label {
    top: 0;
    color: $primary-brand-color;
  }
}

.franchise-form__label {
  @include transition (top);
  display: inline-block;
  padding: 0 10px;
  position: absolute;
  top: 50%;
  left: 20px;
  transform: translateY(-50%);

  font-weight: 500;

  color: $secondary-text-color;
  background-color: $primary-color;

  @media screen and (max-width: 767px) {
    font-size: 12px;
    line-height: 1.62;
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    font-size: 14px;
    line-height: 1.86;
  }
  @media screen and (min-width: $desktop) {
    font-size: 16px;
    line-height: 1.86;
  }
}

.franchise-form__icon-input {
  position: absolute;
  left: 10px;
  top: 50%;
  transform: translateY(-50%);
  fill: $secondary-text-color;
}

sources.sass\\components\\_buttons.scss:
// ----------------------------- CONTACTS BUTTONS --------------------------  //

.contacts-locations-button {
  @extend %button--pink;
  @extend %flex-center-style;

  @media screen and (max-width: 767px) {
    width: 161px;
    margin-bottom: 10px;
  }

  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    width: 191px;
    margin-right: 15px;
  }

  @media screen and (min-width: $desktop) {
    width: 198px;
    margin-right: 15px;
  }
}

.contacts-franchise-button {
  @extend %button--white;
  @extend %flex-center-style;

  @media screen and (max-width: 767px) {
    width: 161px;
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    width: 174px;
  }
  @media screen and (min-width: $desktop) {
    width: 180px;
  }
}

.button__text {
  margin-right: 13px;
}

.button__icon--white {
  stroke: $primary-color;
}

.button__icon--pink {
  stroke: $primary-brand-color;
}

// ---------------------- Header BUTTONS -------------------  //

.header-button {
  @extend %button--white;
  @extend %flex-center-style;

  @media screen and (max-width: 767px) {
    display: none;
  }
  @media screen and (min-width: $tablet) {
    width: 160px;
  }
}

.modal-button {
  @extend %button--white;
  @extend %flex-center-style;
  margin-bottom: 19px;
  margin-left: auto;
  margin-right: auto;
  width: 209px;
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    width: 186px;
  }
  @media screen and (min-width: $desktop) {
    display: none;
  }
}

.modal-button__text {
  margin-right: 16px;
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    margin-right: 12px;
  }
}

.order-button {
  @extend %button--pink;
  @extend %flex-center-style;
  @media screen and (max-width: 767px) {
    width: 209px;
    margin-bottom: 16px;
  }
  @media screen and (min-width: $tablet) {
    width: 186px;
    margin-right: 16px;
  }
}

.close-button {
  @extend %button--white;
  @extend %flex-center-style;
  @media screen and (max-width: 767px) {
    width: 209px;
  }
  @media screen and (min-width: $tablet) {
    width: 186px;
  }
}

.button-wraper {
  display: flex;
  margin: 60px auto 19px;
  @media screen and (max-width: 767px) {
    flex-direction: column;
    align-items: center;
  }
  @media screen and (min-width: $tablet) {
    justify-content: space-around;
  }
}

// ---------------------- FRANCHISE-MODAL BUTTONS -------------------  //

.franchise-close-button {
  @extend %flex-center-style;
  position: absolute;
  top: 20px;
  right: 20px;
  padding: 2px;
  border: none;
  border-radius: 50%;
  background-color: transparent;
  cursor: pointer;

  &:hover,
  &:focus {
    .franchise-form__icon-close {
      transform: scale(1.25);
      fill: $primary-brand-color;
    }
  }
}

.franchise-form__icon-close {
  transform: scale(1);
  transition: fill $transition-duration $transition-timing-function,
    transform $transition-duration $transition-timing-function;
  fill: $secondary-text-color;
}

.franchise-form-button {
  @extend %button--pink;

  @media screen and (max-width: 767px) {
    width: 140px;
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    width: 174px;
  }
  @media screen and (min-width: $desktop) {
    width: 180px;
  }

  &:hover,
  &:focus {
    .franchise-form-button__text {
      transform: scale(1.25);
    }
  }
}

.franchise-form-button__text {
  display: inline-block;
  transform: scale(1);
  transition: transform $transition-duration $transition-timing-function;
}

// ----------------------------------- MAP-MODAL BUTTONS -----------------------------  //

.map-close-button {
  position: absolute;
  top: 0;
  right: 0;
  z-index: 5;
  @extend %flex-center-style;
  border: none;
  border-radius: 50%;
  padding: 5px;
  background-color: $primary-color;
  cursor: pointer;
  transform: scale(1);
  transition: transform $transition-duration $transition-timing-function;

  &:hover,
  &:focus {
    transform: scale(1.25);

    .map-form__icon-close {
      fill: $primary-brand-color;
    }
  }
}

.map-form__icon-close {
  fill: $secondary-text-color;
  transition: fill $transition-duration $transition-timing-function,
    transform $transition-duration $transition-timing-function;
}

sources.sass\\layout\\_header.scss:
.header {
  position: absolute;
  z-index: 20;
  width: 100%;
}

.header-wraper {
  display: flex;
  align-items: center;
  padding-top: 6px;
  justify-content: space-between;
  @media screen and (min-width: $tablet) {
    padding-left: 34px;
    padding-top: 37px;
  }
  @media screen and (min-width: $desktop) {
    padding-left: 46px;
  }
}

.menu-button-wraper {
  display: flex;
  align-items: center;
}

.menu-button {
  width: 20px;
  height: 14px;
  fill: $primary-brand-color;
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    margin-right: 30px;
  }
  @media screen and (min-width: $desktop) {
    display: none;
  }
}

.nav-list {
  @media screen and (min-width: $desktop) {
    display: flex;
  }
}

.nav-list__item {
  @media screen and (min-width: $desktop) {
    &:not(:first-child) {
      margin-left: 35px;
    }
  }
  @media screen and (max-width: 1279px) {
    padding: 13px 0;
    border-bottom: 1px solid $menu-border-color;
    &:first-child {
      border-top: 1px solid $menu-border-color;
    }
  }
}

.nav-list__link {
  @include transition (color);
  padding: 13px 0;
  font-weight: 700;
  font-size: 14px;
  line-height: 1.29;
  color: $primary-color;
  @media screen and (min-width: $desktop) {
    font-size: 16px;
  }
  &:hover,
  &:focus {
    color: $primary-brand-color;
  }
}

sources.sass\\layout\\_hero.scss:
.main-wrapper {
  min-width: 100%;
}

.scrolltop {
  @include transition(background-color);
  @extend %flex-center-style;
  position: fixed;
  bottom: 10px;
  right: 10px;
  border-radius: 50%;
  width: 30px;
  height: 30px;
  opacity: 0;
  z-index: 99;
  background-color: $primary-brand-color;
  @media screen and (min-width: 768px) {
    width: 40px;
    height: 40px;
  }
  @media screen and (min-width: 1280px) {
    bottom: 20px;
    right: 20px;
    width: 50px;
    height: 50px;
  }
  &:hover,
  &:focus {
    background-color: $secondary-brand-color;
  }
}

.showBtn {
  opacity: 1;
}

.scroll-top__btn {
  border: solid $primary-color;
  border-width: 0 2px 2px 0;
  display: inline-block;
  padding: 3px;
  &--up {
    transform: rotate(-135deg);
  }
}

.hero {
  overflow: hidden;
  background-color: $secondary-brand-color;
}

.hero__container {
  position: relative;
  padding-top: 193px;
  padding-bottom: 160px;

  @media screen and (min-width: $tablet) {
    padding-top: 100px;
    padding-bottom: 173px;
  }
  @media screen and (min-width: $desktop) {
    padding-top: 171px;
    padding-bottom: 292px;
    padding-right: 97px;
  }
}

.hero__content {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.title__wrapper {
  z-index: 1;
  position: relative;
  width: 218px;
  @media screen and (min-width: $tablet) {
    width: 162px;
  }
  @media screen and (min-width: $desktop) {
    width: 277px;
  }
}

.title__list {
  display: flex;
  flex-direction: row;
}

.title__link {
  display: block;
  @include font(400, 8px, 1.14, 0.02em);
  padding: 8px 14px;
  border-radius: 20px;
  @media screen and (min-width: $desktop) {
    @include font(400, 14px, 1.29, 0.02em);
    padding: 11px 22px;
  }
}

.title__link--brand-color {
  color: $primary-color;
  background-color: $primary-brand-color;
  @include transition(background-color);
  @include transition(box-shadow);
  &:hover,
  &:focus {
    background-color: $accent-color;
    box-shadow: 0px 4px 4px rgba($primary-text-color, 0.25);
  }
}

.title__link--primary-color {
  color: $primary-brand-color;
  background-color: $primary-color;
  @include transition(background-color);
  @include transition(box-shadow);
  &:hover,
  &:focus {
    background-color: $accent-shadow-color;
    box-shadow: 0px 4px 4px rgba($primary-text-color, 0.25);
  }
}

.title__item:not(last.child) {
  margin-right: 8px;
  @media screen and (min-width: $desktop) {
    margin-right: 15px;
  }
}

.outlets__wrapper {
  z-index: 1;
  @media screen and (max-width: 767px) {
    display: none;
  }
}

.outlets__item {
  position: relative;
}

.outlets__item:not(last.child) {
  margin-bottom: 13px;
  @media screen and (min-width: $desktop) {
  margin-bottom: 35px;
  }
}

.outlets__item::after {
  content: '';
  width: 16px;
  height: 16px;
  background-color: $primary-color;
  position: absolute;
  top: -4px;
  right: -8px;
  border-radius: 50%;
  z-index: -1;

  @media screen and (min-width: $desktop) {
    width: 30px;
    height: 30px;
    right: -16px;
    top: -8px;
  }
}

.outlets__number {
  @include font(400, 28px, 1.14, 0.04em);
  text-align: end;
  font-family: $secondary-font;
  text-transform: uppercase;
  color: $primary-brand-color;
  @media screen and (min-width: $desktop) {
    @include font(400, 46px, 1.15, 0.04em);
  }
}

.outlets__text {
  @include font(400, 8px, 1.25, 0.04em);
  text-align: end;
  color: $primary-color;
  @media screen and (min-width: $desktop) {
  @include font(400, 14px, 1.28, 0.04em);
  }
}

.jug__wrapper {
  @media screen and (max-width: 767px) {
    display: none;
  }
  position: absolute;
  display: flex;
  align-items: center;
  bottom: 28px;
  left: 34px;
  @media screen and (min-width: $desktop) {
    bottom: 45px;
    left: 77px;
  }
}

.animate__animated.animate__bounce {
  --animate-duration: 5s;
  --animate-delay: 2s;
}

.jug__image {
  width: 124px;
  @media screen and (min-width: $desktop) {
    width: 188px;
  }
}

.jug__article {
  overflow: hidden;
  width: 114px;
  @media screen and (min-width: $desktop) {
    width: 200px;
    margin-left: 30px;
  }
}

.jug__text {
  font-family: $primary-font;
  font-style: normal;
  font-weight: normal;
  font-size: 8px;
  line-height: 1.25;
  color: $primary-color;
  @media screen and (min-width: $desktop) {
    font-size: 14px;
    line-height: 1.28;
  }
}

.jug__overlay {
  height: 100%;
  width: 100%;
  transition-property: transform, opasity;
  @include transition(transform);
  transform: translateX(-100%);
}

.jug__button {
  position: absolute;
  bottom: 11px;
  width: 18px;
  height: 18px;
  margin-top: 7px;
  border-radius: 50%;
  background-repeat: no-repeat;
    background-size: contain;
    box-shadow: 0px 4px 4px rgba($primary-text-color, 0.25);
    transform: rotate(0);
    transition: transform 0.5s $transition-timing-function;
    &:hover, &:focus  {
      transform: rotate(-90deg);
    }
  background-image: url(../images/hero/tablet/arrow-right-tablet@1x.png);
  @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
    background-image: url(../images/hero/tablet/arrow-right-tablet@2x.png);
  }

  @media screen and (min-width: $desktop) {
    width: 26px;
    height: 26px;
    margin-top: 20px;
    background-image: url(../images/hero/desktop/arrow-right-desktop@1x.png);
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(../images/hero/desktop/arrow-right-desktop@2x.png);
    }
  }
  &:hover + .jug__overlay {
    transform: translateX(0);
  }
}

.jug__button:hover + .jug__overlay {
  transform: translateX(0);
}

.hero-ellipse__wrapper {
  position: absolute;
  width: 346px;
  height: 344px;
  border-radius: 50%;
  background-color: $tertiary-brand-color;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  right: -36px;
  bottom: 75px;
  @media screen and (min-width: $tablet) {
    width: 324px;
    height: 324px;
    right: 137px;
    bottom: 46px;
  }
  @media screen and (min-width: $desktop) {
    width: 538px;
    height: 538px;
    right: 267px;
    bottom: 75px;
  }
}

.hero-icecream__image {
  width: 171px;
  position: absolute;
  bottom: 0;
  right: 32px;
  @media screen and (min-width: $tablet) {
    width: 143px;
    right: 247px;
  }
  @media screen and (min-width: $desktop) {
    width: 236px;
    right: 450px;
  }
}

.animate__animated.animate__pulse {
  --animate-duration: 2s;
  --animate-delay: 5s;
}

.hero-girl__wrapper {
  @media screen and (max-width: 767px) {
    display: none;
  }
}

.hero-girl__image {
  position: absolute;
  width: 168px;
  bottom: 0;
  right: 34px;
  @media screen and (min-width: $desktop) {
    width: 293px;
    right: 109px;
  }
}

.animate__animated.animate__fadeInUpBig {
  --animate-duration: 2s;
  --animate-delay: 10s;
}

sources.sass\\layout\\_products.scss:
.products {
  padding: 121px 0 98px;

  @media screen and (min-width: $tablet) {
    padding: 122px 0 112px;
  }
  @media screen and (min-width: $desktop) {
    padding: 127px 0 119px;
  }

  &__subtitle {
    text-align: center;
    margin-bottom: 15px;
  }

  &__title {
    text-align: center;
    margin-bottom: 144px;

    @media screen and (min-width: $tablet) {
      margin-bottom: 150px;
    }
    @media screen and (min-width: $desktop) {
      margin-bottom: 259px;
    }
  }

  &__list {
    @media screen and (min-width: $tablet) {
      display: flex;
      flex-wrap: wrap;
      justify-content: center;
      margin: -9px;
    }

    @media screen and (min-width: $desktop) {
      margin: -15px;
    }
  }

  &__item:not(:last-child) {
    margin-bottom: 130px;

    @media screen and (min-width: $tablet) {
      margin: 9px;
    }

    @media screen and (min-width: $desktop) {
      margin: 15px;
    }
  }

  &__item {
    position: relative;
    width: 280px;
    margin-right: auto;
    margin-left: auto;
    border-radius: 24px;
    background-color: $secondary-brand-color;
    text-align: center;

    @media screen and (min-width: $tablet) {
      flex-basis: calc(100% / 3 - 36px);
      width: 220px;
      margin: 9px;
    }

    @media screen and (min-width: $desktop) {
      flex-basis: calc(100% / 3 - 60px);
      width: 330px;
      margin: 15px;
    }
  }

  &__item:first-child {
    background-color: $secondary-brand-color;
  }

  &__item:nth-child(2) {
    background-color: $second-card-color;
  }

  &__item:last-child {
    background-color: $third-card-color;
  }

  &__item:first-child::before,
  &__item:nth-child(2)::before,
  &__item:last-child::before {
    content: '';
    display: block;
    width: 249px;
    height: 252px;
    position: absolute;
    top: -113px;
    left: 16px;
    background-repeat: no-repeat;
    background-size: cover;

    @media screen and (min-width: $tablet) {
      width: 220px;
      height: 226px;
      top: -104px;
      left: 0px;
    }

    @media screen and (min-width: $desktop) {
      width: 335px;
      height: 335px;
      top: -210px;
      left: -3px;
    }
  }

  &__item:first-child::before {
    background-image: url('../images/products/ice_cream-mob-1x.png');

    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url('../images/products/ice_cream-mob-2x.png');
    }

    @media screen and (min-width: $tablet) {
      background-image: url('../images/products/ice_cream-tab-1x.png');

      @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
        background-image: url('../images/products/ice_cream-tab-2x.png');
      }
    }

    @media screen and (min-width: $desktop) {
      background-image: url('../images/products/ice_cream-desc-1x.png');

      @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
        background-image: url('../images/products/ice_cream-desc-2x.png');
      }
    }
  }

  &__item:nth-child(2)::before {
    background-image: url('../images/products/ice_coffee-mob-1x.png');

    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url('../images/products/ice_coffee-mob-2x.png');
    }

    @media screen and (min-width: $tablet) {
      background-image: url('../images/products/ice_coffee-tab-1x.png');

      @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
        background-image: url('../images/products/ice_coffee-tab-2x.png');
      }
    }

    @media screen and (min-width: $desktop) {
      background-image: url('../images/products/ice_coffee-desc-1x.png');

      @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
        background-image: url('../images/products/ice_coffee-desc-2x.png');
      }
    }
  }

  &__item:last-child::before {
    background-image: url('../images/products/milkshakes-mob-1x.png');

    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url('../images/products/milkshakes-mob-2x.png');
    }

    @media screen and (min-width: $tablet) {
      background-image: url('../images/products/milkshakes-tab-1x.png');

      @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
        background-image: url('../images/products/milkshakes-tab-2x.png');
      }
    }

    @media screen and (min-width: $desktop) {
      background-image: url('../images/products/milkshakes-desc-1x.png');

      @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
        background-image: url('../images/products/milkshakes-desc-2x.png');
      }
    }
  }

  &__item-wrap {
    position: relative;
    overflow: hidden;
    width: 280px;
    height: 485px;
    padding: 161px 40px 41px;

    @media screen and (min-width: $tablet) {
      width: 220px;
      padding: 152px 11px 40px;
    }

    @media screen and (min-width: $desktop) {
      width: 330px;
      height: 525px;
      padding: 163px 44px 50px;
    }
  }

  &__item-title {
    font-family: $secondary-font;
    @include font(400, 20px, 1.15, 0.04em);
    color: $primary-color;

    @media screen and (min-width: $desktop) {
      @include font(400, 30px, 1.13, 0.04em);
    }
  }

  &__item-title::after {
    content: '';
    display: block;
    width: 26px;
    height: 6px;
    margin: 30px auto;
    background-image: url('../images/products/dots-1x.png');
    background-repeat: no-repeat;
    background-size: cover;

    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url('../images/products/dots-2x.png');
    }

    @media screen and (min-width: $desktop) {
      margin: 32px auto;
    }
  }

  &__item-text {
    @include font(700, 14px, 1.52, 0.04em);
    color: $primary-color;
    margin-bottom: 30px;

    @media screen and (min-width: $tablet) {
      @include font(700, 14px, 1.76, 0.04em);
      margin-bottom: 40px;
    }

    @media screen and (min-width: $desktop) {
      @include font(700, 16px, 1.76, 0.04em);
      margin-bottom: 62px;
    }
  }

  &__item-overlay {
    position: absolute;
    top: 0;
    left: 7.5%;
    height: 77%;
    width: 85%;
    transform: translateY(130%);
    transition: transform 0.5s $transition-timing-function;
    border-radius: 24px;
    padding: 160px 10px;
  }

  &__item:first-child &__item-overlay {
    background-color: rgba($secondary-brand-color, 0.9);
  }
  &__item:nth-child(2) &__item-overlay {
    background-color: rgba($second-card-color, 0.9);
  }
  &__item:last-child &__item-overlay {
    background-color: rgba($third-card-color, 0.9);
  }

  &__button:hover + &__item-overlay {
    transform: translateY(0);
  }

  &__item-details {
    @include font(400, 14px, 1.15, 0.04em);
    color: $primary-color;

    @media screen and (min-width: $desktop) {
      @include font(400, 16px, 1.5, 0.04em);
    }
  }

  &__button {
    position: relative;
    z-index: 99;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-image: url('../images/products/arrow-right-1x.png');
    background-repeat: no-repeat;
    background-size: cover;
    box-shadow: 0px 4px 4px rgba($primary-text-color, 0.25);
    transform: rotate(0);
    transition: transform 0.5s $transition-timing-function;
    &:hover, &:focus  {
      transform: rotate(-90deg);
    }
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url('../images/products/arrow-right-2x.png');
    } 
  }
}

sources.sass\\layout\\_about.scss:
.about {
    padding: 125px 0 39px;
    background-color: $secondary-color;

  @media screen and (min-width:$tablet) {
    padding: 130px 0 66px;
  }

  @media screen and (min-width:$desktop) {
    padding: 125px 0 39px;
  }

