sources.sass\\layout\\_customer-reviews.scss:
.reviews {
    padding-top: 80px;
    padding-bottom: 86px;
  @media screen and (min-width: $tablet) {
    padding-top: 121px;
    padding-bottom: 119px;
  }
  @media screen and (min-width: $desktop) {
    padding-top: 123px;
    padding-bottom: 132px;
  }
  &__client-wraper {
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  &__image {
    width: 85px;
    height: 85px;
    margin-bottom: 29px;
    text-align: center;
    @media screen and (min-width:768px){ 
        margin-bottom: 19px;
    }
    @media screen and (min-width:1280px){ 
        margin-bottom: 15px;
    }
  }

  &__text-wraper {
    position: relative;
    width: 280px;
    margin-bottom: 30px;
    background-color: $shadow-background-color;
    padding: 43px 66px 26px 57px;
    z-index: -1;
    @media screen and (min-width:480px){ 
        width: 440px;
    }
    @media screen and (min-width:768px){ 
        margin-bottom: 24px;
        padding: 47px 80px 34px 80px;
        width: 620px;
        background-color: rgba($shadow-background-color, 0.4);
    }
    @media screen and (min-width:1280px){ 
        margin-bottom: 15px;
        padding: 47px 89px 39px 87px;
        width: 925px;
        background-color: $shadow-background-color;
    }
  }
  &__text {
    @include font(500, 14px, 2.16, 0.04em);

    text-align: center;
    color: $primary-text-color;
    background-size: 60px 60px;
    background-image: linear-gradient(
    0deg,
    rgb(225, 225, 225) 2%,
    rgba(255, 255, 255, 0) 2%,
    rgba(255, 255, 255, 0) 50%,
    rgb(225, 225, 225)50%,
    rgb(225, 225, 225) 52%,
    rgba(255, 255, 255, 0) 52%,
    rgba(255, 255, 255, 0) 100%
  );
    z-index: 1;
    @media screen and (min-width:1280px){ 
      @include font(500, 16px, 1.86, 0.04em);

    }
    &::before{
    content:'';
    position: absolute;
    top: 28px;
    left: 61px;
    display: block;
    background-image: url(../images/customer-reviews/“-1x.webp);
    width: 54px;
    height: 40px;
    z-index: -1;
    @media screen and (min-width:768px){ 
       top: 30px;
       left: 40px;
    }
    @media screen and (min-width:1280px){ 
        top: 32px;
        left: 50px;
    }
  }
   
  }

  &__author {
    margin-bottom: 30px;
    font-weight: 500;
    line-height: 186%;
    @media screen and (max-width: 767px) {
      font-size: 14px;
    }
    @media screen and (min-width: $tablet) {
      font-size: 16px;
    }
    @media screen and (min-width: $desktop) {
      font-size: 20px;
    }
    &::before{
      content: "";
      display: block;
      margin-right: auto;
      margin-left: auto;
      margin-bottom: 18px;
      width: 32px;
      height: 4px;
      color: $accent-color;
      background-image: url(../images/customer-reviews/customers-reviews-pagination-dots.svg);
      @media screen and (min-width:768px){ 
          margin-bottom: 15px;
       }
       
  }
  }
}

.slick-next {
  display: none !important;
}
.slick-list {
  @media screen and (min-width: $desktop) {
    height: 319px !important;
  }
}

.slick-dots li.slick-active button{
  &::before {
    background-image: url(../images/customer-reviews/slick-active-btn.svg);
    font-size: 0px !important;
    opacity: 1 !important;
    color: transparent !important;
    background-repeat: no-repeat;
    background-position: center;
    content: "" !important;
  }
}
.slick-dots li button{
  &:hover::before {
    color: $accent-color !important;
  }
}
.slick-dots li button::before {
font-size: 12px !important;
}

sources.sass\\layout\\_contacts.scss:
.contacts {
  @media screen and (max-width: 767px) {
    padding-bottom: 113px;
    background-image: linear-gradient(to bottom, $primary-color 84px, $secondary-color 84px);
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    padding-bottom: 158px;
    background-image: linear-gradient(to bottom, $primary-color 78px, $secondary-color 78px);
  }
  @media screen and (min-width: $desktop) {
    padding-bottom: 165px;
    background-image: url(../images/contacts/contacts-bgi-1x.png),
      linear-gradient(to bottom, $primary-color 102px, $secondary-color 102px);
    background-position-y: 102px, 0;
    background-repeat: no-repeat;
    background-size: contain;

    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(../images/contacts/contacts-bgi-2x.png),
        linear-gradient(to bottom, $primary-color 102px, $secondary-color 102px);
      background-position-y: 102px, 0;
      background-size: contain;
    }
  }
}

.contacts__container {
  @media screen and (max-width: 767px) {
    margin-bottom: 54px;
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    margin-bottom: 81px;
  }
  @media screen and (min-width: $desktop) {
    margin-bottom: 86px;
  }
}

.contacts-list {
  display: flex;

  @media screen and (max-width: 767px) {
    flex-direction: column;
    align-items: center;
  }
  @media screen and (min-width: $tablet) {
    justify-content: center;
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    margin: 0 -10px 0 -10px;
  }
  @media screen and (min-width: $desktop) {
    margin: 0 -15px 0 -15px;
  }
}

.contacts-list__item {
  box-shadow: 0px 8px 30px rgba(212, 20, 67, 0.102);
  border-radius: 24px;
  background: $primary-color;

  @media screen and (max-width: 1279px) {
    width: 220px;
  }
  @media screen and (max-width: 767px) {
    padding: 43px 20px;
    &:not(:last-child) {
      margin-bottom: 20px;
    }
  }
  @media screen and (min-width: $tablet) {
    padding: 50px 31px;
    margin: 0 10px 0 10px;
  }
  @media screen and (min-width: $desktop) {
    width: 330px;
    padding: 51px 43px 62px 43px;
    margin: 0 15px 0 15px;
  }
}

.contacts-list__category {
  @extend %flex-center-style;
  @include font(500, 12px, 1.86, 0.04em);
  color: $primary-color;

  @media screen and (max-width: 767px) {
    margin-bottom: 25px;
    height: 16px;
    border-radius: 4px;
  }
  @media screen and (min-width: $tablet) {
    margin-bottom: 30px;
    height: 28px;
    font-size: 16px;
    border-radius: 6px;
  }
  @media screen and (min-width: $desktop) {
    margin-bottom: 32px;
  }
}

.contacts-list__category--main {
  background-color: $second-card-color;

  @media screen and (max-width: 767px) {
    width: 80px;
  }
  @media screen and (min-width: $tablet) {
    width: 73px;
  }
}

.contacts-list__category--secondary {
  background-color: $third-card-color;

  @media screen and (max-width: 767px) {
    width: 130px;
  }
  @media screen and (min-width: $tablet) {
    width: 138px;
  }
}

.contacts-list__city {
  @include font(500, 12px, 1.86, 0.04em);
  @media screen and (max-width: 767px) {
    margin-bottom: 10px;
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    margin-bottom: 14px;
    font-size: 14px;
  }
  @media screen and (min-width: $desktop) {
    margin-bottom: 10px;
    font-size: 16px;
  }
}

.contacts-list__text {
  @include font(500, 12px, 1.5, 0.04em);
  color: $secondary-text-color;
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    @include font(500, 14px, 1.66, 0.04em);
  }
  @media screen and (min-width: $desktop) {
    @include font(500, 16px, 1.83, 0.04em);
  }
}

.contacts-list__border-line {
  position: relative;
  @media screen and (max-width: 767px) {
    margin-bottom: 50px;
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    margin-bottom: 60px;
  }
  @media screen and (min-width: $desktop) {
    margin-bottom: 64px;
  }

  &::after {
    content: '';
    position: absolute;
    display: block;
    width: 100%;
    height: 1px;
    background-color: $border-color;

    @media screen and (max-width: 767px) {
      bottom: -25px;
    }
    @media screen and (min-width: $tablet) and (max-width: 1279px) {
      bottom: -30px;
    }
    @media screen and (min-width: $desktop) {
      bottom: -32px;
    }
  }
}

.contacts-list__weekday-block {
  @media screen and (max-width: 767px) {
    margin-bottom: 10px;
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    margin-bottom: 16px;
  }
  @media screen and (min-width: $desktop) {
    margin-bottom: 15px;
  }
}

.contacts-list__info {
  @include font(500, 12px, 1.86, 0.04em);
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    font-size: 14px;
  }
  @media screen and (min-width: $desktop) {
    font-size: 16px;
  }
}

.contacts-list__schedule {
  @include font(500, 12px, 1.86, 0.04em);
  color: $tertiary-brand-color;
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    font-size: 14px;
  }
  @media screen and (min-width: $desktop) {
    font-size: 16px;
  }
}

.contacts-list__link {
  @include transition (color);
  @include font(500, 12px, 1.5, 0.04em);
  display: block;
  font-style: normal;
  color: $secondary-text-color;
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    @include font(500, 14px, 1.83, 0.04em);
  }

  @media screen and (min-width: $desktop) {
    font-size: 16px;
  }
  &:hover,
  &:focus {
    color: $primary-brand-color;
  }
}

.contacts-buttons {
  // width: 160px;
  display: flex;

  @media screen and (max-width: 767px) {
    // margin-top: 54px;
    flex-direction: column;
    align-items: center;
  }
  @media screen and (min-width: $tablet) and (max-width: 1279px) {
    // margin-top: 81px;
    justify-content: center;
  }
  @media screen and (min-width: $desktop) {
    // margin-top: 86px;
    justify-content: center;
  }
}

sources.sass\\layout\\_footer.scss:
.footer{
    background-color:  $secondary-color;
}

.footer-wrapper{
    @media screen and (min-width:$tablet){
    display: flex;
    justify-content: space-between;
    padding-bottom: 91px;
    }

    @media screen and (min-width:$desktop){
    padding-bottom: 97px;
    }
}

@media screen and (max-width:767px){
.footer .subtitle {
    max-width: 232px;
    margin-bottom: 17px;
}
.footer .title {
    max-width: 200px;
}
}

@media screen and (min-width:$tablet){
.footer .subtitle {
    max-width: 340px;
    margin-bottom: 16px;
}
.footer .title {
    max-width: 340px;
}
}

@media screen and (min-width:$desktop){
.footer .subtitle {
    max-width: 472px;
    margin-bottom: 15px;
}
.footer .title {
    max-width: 477px;
}
}

.social-list {
    display: flex;
    justify-content: flex-end;
    margin-bottom: 46px;
		&__link {
            @extend %flex-center-style;
            background-color: $tertiary-brand-color;
            height: 32px;
            width: 32px;
            border-radius: 50%;
		}
}

.social-list__item:not(:last-child){
    @media screen and (max-width:767px){
        margin-right: 28px;
    }

    @media screen and (min-width:$tablet){
        margin-right: 30px;
    }

    @media screen and (min-width:$desktop){
        margin-right: 24px;
    }
    
}

.social-list__link:focus, .social-list__link:hover {
    @include transition (background-color);
    background-color: $primary-brand-color;
}
.title-wrapper, .contacts-wrapper {
    @media screen and (max-width:767px){
    padding-bottom: 43px;
    }
}
.contact-tel {
@include font(700, 12px, 1.86, 0.04em);
text-align: right;
@media screen and (min-width:$tablet){
    font-size: 14px;
}
@media screen and (min-width:$desktop){
    font-size: 16px;
}
	&__link {
    font-weight: 500;
    color: $primary-brand-color;
	}
}

.copyright {
padding-top: 22px;
padding-bottom: 25px;
border-top: solid 1px $border-color;
@media screen and (min-width:$tablet){
padding-top: 29px;
padding-bottom: 18px;
}
@media screen and (min-width:$desktop){
    padding-top: 9px;
    padding-bottom: 10px;
}
	&__text {
    @include font(700, 14px, 1.5, 0.04em);
    
    max-width: 150px;
    text-align: center;
    margin: 0 auto;
    color: $secondary-text-color;
    @media screen and (min-width:$tablet){
     line-height: 1.86;   
     max-width: 100%;
    }
	}
}
